document.addEventListener("DOMContentLoaded", function() {
// Function to create a new list item with a link and insert it as the first child
    function createAndInsertFirst(text, link, extraClass) {
      var myList = document.getElementById("logo_right_wrapper");
      var li = document.createElement("li");
      li.classList.add("book-li"); // Add the custom class to the li
      var a = document.createElement("a");
      a.textContent = text;
      a.href = link;
      if (extraClass) {
        a.classList.add(extraClass);
      }
      li.appendChild(a);
      myList.insertBefore(li, myList.firstChild);
    }

    // Adding list items dynamically as the first child
    createAndInsertFirst("Book Now", "https://explorelogicsit.net/bestworldvision/book-now/", "booking-btn");
});
